package com.asiabill.testapp.one.model;import android.text.TextUtils;import org.json.JSONException;import org.json.JSONObject;public class PayResult {	private String code;	private String message;	private String optionl;	public PayResult(String rawResult) {		if (TextUtils.isEmpty(rawResult))			return;		try {			JSONObject obj = new JSONObject(rawResult);			code = obj.getString("code");			message = obj.getString("message");			optionl = obj.getString("optionl");		} catch (JSONException e) {			e.printStackTrace();		}			}	@Override	public String toString() {		return "PayResult{" +				"code='" + code + '\'' +				", message='" + message + '\'' +				", optionl='" + optionl + '\'' +				'}';	}	public void setCode(String code) {		this.code = code;	}	public void setMessage(String message) {		this.message = message;	}	public void setOptionl(String optionl) {		this.optionl = optionl;	}	public String getCode() {		return code;	}	public String getMessage() {		return message;	}	public String getOptionl() {		return optionl;	}}